    this.tabContainer.getAttribute("overflow") == "true" ||
    !Services.prefs.getBoolPref("browser.tabs.animate")) {
  t.setAttribute("fadein", "true");
  setTimeout(function (tabContainer) {
    tabContainer._handleNewTab(t);
  }, 0, this.tabContainer);
